module Damm
  module Tables
    TableMissingError = Class.new(ArgumentError)
    InvalidOrderError = Class.new(ArgumentError)

    # Tables for the Damm algorithm
    # More tables can be found at:
    # https://en.wikipedia.org/wiki/Damm_algorithm
    # and
    # http://www.md-software.de/math/DAMM_Quasigruppen.txt

    def get_tas_table(order)
      case order
      when 10, 17, 49
        TAS_TABLE_ORDER[order]
      when 2, 6
        fail InvalidOrderError,
             "Totally anti-symmetric quasigroup table for order #{order} does not exist"
      else
        fail TableMissingError,
             "Totally anti-symmetric quasigroup table for order #{order} not supported"
      end
    end

